mapper.update:
update Diary
set
d_memo=#{d_memo}, d_special=#{d_special}
where
d_date=#{d_date}

update Diary set
d_memo=#{d_memo}, d_img=#{d_img}, d_special=#{d_special}
where
d_date=#{d_date}

update Diary set
d_flag=1
where d_date=#{d_date}

mapper.select:
select count(*) as count, sum(w_distance) as distance, sum(w_time) as time
from Walk 
where peid=#{peid} and date(w_date)=#{d_date} and w_flag=0;
