mapper.select:
select pid
from Place
where p_latitude=#{p_latitude} and p_longtitude = #{p_longtitude} and p_flag=0;

insert into Place(p_latitude, p_longtitude, p_like, p_flag)
values(#{p_latitude}, #{p_longtitude}, 1, 0)
returning pid;

insert into LikePlace(wid, pid, peid, l_image, l_desc, p_location, l_like, l_flag, l_date)
values(#{wid}, #{pid}, #{peid},  #{l_image}, #{l_desc}, #{p_location}, 0, 0, now())
returning lid;

select count(*)
from LikePlace
where peid=#{peid} and pid=#{pid} and wid=#{wid} and l_flag=0;

mapper.update:
update Place
set p_like = p_like + 1
where pid=#{pid};

mapper.select:
select count(*)
from LikePlace
where lid=#{lid} and peid= #{peid} and l_flag=0;;

mapper.update:
update LikePlace
set l_image = #{l_image}, l_desc = #{l_desc}
where lid=#{lid};

mapper.select:
select pid
from LikePlace
where lid=#{lid};

mapper.update:
update LikePlace
set l_flag = 1
where lid=#{lid};

update Place
set p_like = p_like - 1
where pid=#{pid} and p_like!=0;

mapper.select:
select lid
from LikePost
where lid=#{lid} and uid = #{uid};

mapper.update:
update LikePlace
set l_like = l_like + 1
where lid=#{lid};

update LikePlace
set l_like = l_like - 1
where lid=#{lid};

mapper.select:
select l.lid, l.l_image, l.l_desc, l.l_date, pl.p_latitude, pl.p_longtitude, p.pe_name
from LikePlace as l join Pet as p on l.peid = p.peid join Place as pl on l.pid = pl.pid
where l.lid=#{lid} and l.l_flag=0;
