mapper.select:
select pid
from Place
where p_latitude=#{p_latitude} and p_longtitude = #{p_longtitude};

insert into Place(p_latitude, p_longtitude, p_like, p_flag)
values(#{p_latitude}, #{p_longtitude}, 1, 0)
returning pid;

select count(*)
from LikePlace
where peid=#{peid} and pid = #{pid};

mapper.update:
update Place
set p_like = p_like + 1
where pid=#{pid};

mapper.select:
select count(*)
from LikePlace
where lid=#{lid} and uid = #{login_id};

mapper.update:
update LikePlace
set l_image = #{l_image}, l_desc = #{l_desc}
where lid=#{lid};

update LikePlace
set l_flag = 1
where lid=#{lid};

update Place
set p_like = p_like - 1
where pid=#{pid} and p_like!=0;
