mapper.update:
update Walk
set w_img=#{url}
where wid=#{wid};

update Walk
set w_distance=#{w_distance}, w_time=#{w_time}
where wid=#{wid};

mapper.select:
select count(*)
from Place
where p_latitude=#{p_latitude} and p_longtitude = #{p_longtitude}

select pid
from Place
where p_latitude=#{p_latitude} and p_longtitude = #{p_longtitude}

mapper.update:
update Likeplace
set p_hate = p_hate + 1
where pid=#{pid};

update Likeplace
set p_like = p_like + 1
where pid=#{pid};
