mapper.update:
update Walk
set w_img=#{url}
where wid=#{wid};

mapper.select:
select count(*)
from Place
where p_latitude=#{p_latitude} and p_longtitude = #{p_longtitude}

insert into Place(p_latitude, p_longtitude, p_like, p_flag)
values(#{p_latitude}, #{p_longtitude}, 1, 0)
returning pid;

mapper.update:
update Likeplace
set p_like = p_like + 1
where pid=#{pid};

mapper.select:
select pid
from Place
where p_latitude=#{p_latitude} and p_longtitude = #{p_longtitude}
